import React, { useRef, useState } from "react";
import '../styles/tour-details.css';
import { Container, Row, Col, Form, ListGroup } from 'reactstrap';
import { useParams } from 'react-router-dom';
import tourData from '../assets/data/tours';
import calculateAvgRating from "../utils/avgRating";
import avatar from "../assets/images/avatar.jpg";
import Booking from "../assets/components/Booking/Booking";  
import Newsletter from "../"; // Correct import

const TourDetails = () => {
  const { id } = useParams();
  const reviewMsgRef  = useRef('');
  const [tourRating ,setTourRating] = useState(null);

  const tour = tourData.find(tour => tour.id === id);

  const {
    photo,
    title,
    desc,
    price,
    address,
    reviews,
    city,
    distance,
    maxGroupSize
  } = tour || {};

  const { totalRating, avgRating } = calculateAvgRating(reviews || []);

  const options = { day: 'numeric', month: 'long', year: 'numeric' };

  const submitHandler = e => {
    e.preventDefault();
    const reviewText = reviewMsgRef.current.value;
    alert(`${reviewText}, ${tourRating}`);
  };

  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg='8'>
              <div className="tour__content">
                <img src={photo} alt="" />
                <div className="tour__info">
                  <h2>{title}</h2>
                  <div className="d-flex align-items-center gap-5">
                    <span className="d-flex align-items-center gap-1">
                      <i className="ri-star-s-fill" style={{ color: "var(--secondary-color)" }}></i>
                      {avgRating === 0 ? null : avgRating}
                      {totalRating === 0 ? "Not rated" : (
                        <span>({reviews?.length})</span>
                      )}
                    </span>

                    <span>
                      <i className="ri-map-pin-fill"></i> {address}
                    </span>
                  </div>

                  <div className="tour__extra-details">
                    <span><i className="ri-map-pin-2-line"></i>{city}</span>
                    <span><i className="ri-money-dollar-circle-line"></i>₹{price}/per person</span>
                    <span><i className="ri-money-dollar-circle-line"></i>{distance} km</span>
                    <span><i className="ri-group-line"></i>{maxGroupSize} people</span>
                  </div>

                  <h5>Description</h5>
                  <p>{desc}</p>
                </div>

                {/*===== Tour reviews section =====*/}
                <div className="tour__reviews mt-4">
                  <h4>Reviews ({reviews?.length} reviews)</h4>

                  <Form onSubmit={submitHandler}>
                    <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                      <span onClick={() => setTourRating(1)}>1<i className="ri-star-s-fill"></i></span>
                      <span onClick={() => setTourRating(2)}>2<i className="ri-star-s-fill"></i></span>
                      <span onClick={() => setTourRating(3)}>3<i className="ri-star-s-fill"></i></span>
                      <span onClick={() => setTourRating(4)}>4<i className="ri-star-s-fill"></i></span>
                      <span onClick={() => setTourRating(5)}>5<i className="ri-star-s-fill"></i></span>
                    </div>

                    <div className="reviews__input">
                      <input type="text" ref={reviewMsgRef} placeholder="Share your thoughts" required />
                      <button className="btn primary__btn text-white" type="submit">Submit</button>
                    </div>
                  </Form>

                  <ListGroup className="user__reviews">
                    {reviews?.map((review, index) => (
                      <div className="review__item" key={index}>
                        <img src={avatar} alt="" />
                        <div className="w-100">
                          <div className="d-flex align-items-center justify-content-between">
                            <div>
                              <h5>{review.username || "Anonymous"}</h5>
                              <p>{new Date(review.date || '01-18-2023').toLocaleDateString("en-US", options)}</p>
                            </div>
                            <span className="d-flex align-items-center">
                              {review.rating}<i className="ri-star-s-fill"></i>
                            </span>
                          </div>

                          <h6>{review.comment || "No comment provided"}</h6>
                        </div>
                      </div>
                    ))}
                  </ListGroup>
                </div>
                {/*===== Tour reviews section end =====*/}
              </div>
            </Col>

            <Col lg='4'>
              <Booking tour={tour} avgRating={avgRating} />
            </Col>
          </Row>
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default TourDetails;
